function find_treasure(instructions) {
  var dirs = [ [1,0], [0,1], [-1,0], [0,-1] ];
  var x=0, y=0, d=1;
  for (var i=0; i<instructions.length; i++) {
    var instr = instructions[i];
    if (instr=='M') { x += dirs[d][0]; y += dirs[d][1]; }
    if (instr=='L') d = ( d+1 ) % 4;
    if (instr=='R') d = ( d-1 + 4 ) % 4;
  }
  return [x,y];
}

